import React, { Component } from 'react'
import { View, Text,styles } from 'react-native';
import { TouchableOpacity } from 'react-native-gesture-handler';

class SplashScreen extends Component {
    render() {


      return (
        <TouchableOpacity style={{flex: 1, backgroundColor:"orange", justifyContent: "center", alignContent: "center"}}>
          <Text style={{color:'white', textAlign:"center", fontWeight: "bold", fontSize: 40}}>
            Splash Screen
          </Text>
        </TouchableOpacity>
      );
    }
  }

export default SplashScreen;
